author=";s:4:"slug";s:19:"1:welcome-to-joomla";s:7:"catslug";s:13:"1:latest-news";}i:4;O:8:"stdClass":32:{s:2:"id";s:2:"42";s:5:"title";s:11:"Newsflash 4";s:5:"alias";s:11:"newsflash-4";s:11:"title_alias";s:0:"";s:9:"introtext";s:274:"Yesterday all servers in the U.S. went out on strike in a bid to get more RAM and better CPUs. A spokes person said that the need for better RAM was due to some fool increasing the front-side bus speed. In future, buses will be told to slow down in residential motherboards.";s:8:"fulltext";s:0:"";s:5:"state";s:1:"1";s:9:"sectionid";s:1:"1";s:4:"mask";s:1:"0";s:5:"catid";s:1:"3";s:7:"created";s:19:"2006-10-12 00:25:50";s:10:"created_by";s:2:"62";s:16:"created_by_alias";s:0:"";s:8:"modified";s:19:"2007-11-08 12:17:20";s:11:"modified_by";s:2:"62";s:11:"checked_out";s:1:"0";s:16:"checked_out_time";s:19:"0000-00-00 00:00:00";s:10:"publish_up";s:19:"2006-10-11 06:00:00";s:12:"publish_down";s:19:"0000-00-00 00:00:00";s:6:"images";s:0:"";s:4:"urls";s:0:"";s:7:"attribs";s:231:"show_title=
