author=";s:4:"slug";s:41:"49:how-do-i-boot-from-live-cd-or-live-usb";s:7:"catslug";s:10:"31:general";}i:1;O:8:"stdClass":32:{s:2:"id";s:2:"48";s:5:"title";s:51:"Quels sont les login / mots de passe par défaut  ?";s:5:"alias";s:47:"quels-sont-les-login--mots-de-passe-par-defaut-";s:11:"title_alias";s:0:"";s:9:"introtext";s:131:"Les logins / mots de passe par défaut sont contenus dans un fichier texte nommé PASWWORD.txt sur le bureau de votre serveur Ciws.";s:8:"fulltext";s:0:"";s:5:"state";s:1:"1";s:9:"sectionid";s:1:"3";s:4:"mask";s:1:"0";s:5:"catid";s:2:"31";s:7:"created";s:19:"2008-10-20 12:37:20";s:10:"created_by";s:2:"62";s:16:"created_by_alias";s:0:"";s:8:"modified";s:19:"2008-10-20 12:39:54";s:11:"modified_by";s:2:"62";s:11:"checked_out";s:1:"0";s:16:"checked_out_time";s:19:"0000-00-00 00:00:00";s:10:"publish_up";s:19:"2008-10-20 12:37:20";s:12:"publish_down";s:19:"0000-00-00 00:00:00";s:6:"images";s:0:"";s:4:"urls";s:0:"";s:7:"attribs";s:309:"created_by=62
