author=";s:4:"slug";s:19:"22:whats-new-in-052";s:7:"catslug";s:14:"29:the-servers";}i:3;O:8:"stdClass":32:{s:2:"id";s:1:"1";s:5:"title";s:29:"Bienvenue sur Cooperation-iws";s:5:"alias";s:29:"bienvenue-sur-cooperation-iws";s:11:"title_alias";s:0:"";s:9:"introtext";s:10326:"<h3>Cooperation IWS est un projet de serveur web intranet nomade:</h3> <ol><li> Un <strong>serveur web</strong>, pour proposer des services d’hébergement de sites web (Blog - Traitement de texte, Wiki, Gestion électronique de documents, Forum, galleries d’images - retouche d’images , portail eLearning, …) destinés au travail collaboratif ou à l’ingénierie de la connaissance.</li><li><strong> Intranet</strong>, pour disposer de ces services où l’on veut: chez soi, dans un cadre associatif ou un cadre professionnel, sans forcément publier le contenu sur le World Wide Web.</li><li><strong> Nomade</strong>, pour emmener ces services avec soi sur une clef usb, un disque dur usb ou un cdrom / dvdrom.</li></ol> <h3><strong>Cooperation-iws est distribué gratuitement sous licence open source.</strong></h3><p>&lt;?php </p><p>$xml_file="cooperation-wui-fr.xml"; </p><p>$tree = simplexml_load_file('../'.$xml_file);<br />                        $count_xml_elt=0;<br />                        foreach($tree-&gt;item as $item) {    <br />                            $array_xml[$count_xml_elt][0] = $item-&gt;item_category;<br />                            $array_xml[$count_xml_elt][1] = $item-&gt;item_name;<br />                            $array_xml[$count_xml_elt][2] = $item-&gt;item_desc;<br />                            $array_xml[$count_xml_elt][3] = $item-&gt;item_url;<br />                            $array_xml[$count_xml_elt][4] = $item-&gt;item_admin_url;<br />                            $array_xml[$count_xml_elt][0] = strval($array_xml[$count_xml_elt][0]);<br />                            $array_xml[$count_xml_elt][1] = strval($array_xml[$count_xml_elt][1]);<br />                            $array_xml[$count_xml_elt][2] = strval($array_xml[$count_xml_elt][2]);<br />                            $array_xml[$count_xml_elt][3] = strval($array_xml[$count_xml_elt][3]);<br />                            $array_xml[$count_xml_elt][4] = strval($array_xml[$count_xml_elt][4]);<br />                        $count_xml_elt++;<br />                        }<br />                        foreach ($array_xml as $key =&gt; $row) {<br />                            $category[$key]  = $row['0'];<br />                            $item_name[$key] = $row['1'];<br />                            $item_desc[$key] = $row['2'];<br />                            $item_url[$key] = $row['3'];<br />                            $item_admin_url[$key] = $row['4'];<br /><br /><br /><br />                        }<br />                        array_multisort($category, SORT_ASC, $item_name, SORT_ASC, $array_xml);<br /><br />?&gt;</p><h1>Applications</h1><p>&lt;?php<br /><br />                       $prev_category = "";<br />                        for($count_boucle=0; $count_boucle &lt;= $count_xml_elt ; $count_boucle++) {<br />                            $category= $array_xml[$count_boucle][0];<br />                            if ("$prev_category" != "$category"){<br />                                echo "&lt;hr&gt;&lt;br&gt;\n&lt;h3&gt;&lt;a name=\"".$count_boucle."\"&gt;&lt;/a&gt;".$category."&lt;/h3&gt;&lt;br&gt;\n";<br />                            }<br />                            $prev_category = $category;<br />                            $name = $array_xml[$count_boucle][1];<br />                            $desc = $array_xml[$count_boucle][2];<br />                            $url = $array_xml[$count_boucle][3];<br />                            $admin_url =$array_xml[$count_boucle][4];<br />                            $url = str_replace ('localhost', $HTTP_SERVER_VARS["SERVER_NAME"], $url);<br />                            $admin_url = str_replace ('localhost', $HTTP_SERVER_VARS["SERVER_NAME"], $admin_url);<br /><br />                            echo "&lt;b&gt;".$name."&lt;/b&gt;&lt;br&gt;\n";<br />                            echo "&lt;a href=\"".$url."\"&gt;".$url."&lt;/a&gt;";<br />                            if ($admin_url) {<br />                                echo " | &lt;a href=\"".$admin_url."\"&gt;admin&lt;/a&gt;\n";<br />                            }<br />                            echo "&lt;br&gt;&lt;br&gt;&lt;br&gt;\n";<br /><br />                            }<br /><br />        <br /><br /><br /> ?&gt; <br />&lt;?php $tree_admin = simplexml_load_file('../admin/'.$xml_file);<br />                        $count_xml_elt=0;<br />                        foreach($tree_admin-&gt;item as $item) {    <br />                            $array_admin_xml[$count_xml_elt][0] = $item-&gt;item_category;<br />                            $array_admin_xml[$count_xml_elt][1] = $item-&gt;item_name;<br />                            $array_admin_xml[$count_xml_elt][2] = $item-&gt;item_desc;<br />                            $array_admin_xml[$count_xml_elt][3] = $item-&gt;item_url;<br />                            $array_admin_xml[$count_xml_elt][4] = $item-&gt;item_admin_url;<br />                            $array_admin_xml[$count_xml_elt][0] = strval($array_admin_xml[$count_xml_elt][0]);<br />                            $array_admin_xml[$count_xml_elt][1] = strval($array_admin_xml[$count_xml_elt][1]);<br />                            $array_admin_xml[$count_xml_elt][2] = strval($array_admin_xml[$count_xml_elt][2]);<br />                            $array_admin_xml[$count_xml_elt][3] = strval($array_admin_xml[$count_xml_elt][3]);<br />                            $array_admin_xml[$count_xml_elt][4] = strval($array_admin_xml[$count_xml_elt][4]);<br />                        $count_xml_elt++;<br />                        }<br />                        foreach ($array_admin_xml as $key_admin =&gt; $row_admin) {<br />                            $item_admin_category[$key_admin]  = $row_admin['0'];<br />                            $item_admin_name[$key_admin] = $row_admin['1'];<br />                            $item_admin_desc[$key_admin] = $row_admin['2'];<br />                            $item_url[$key_admin] = $row_admin['3'];<br />                            $item_admin_admin_url[$key_admin] = $row_admin['4'];<br /><br /><br /><br />                        }<br />                        array_multisort($item_admin_category, SORT_ASC, $item_admin_name, SORT_ASC, $array_admin_xml);<br /><br />?&gt;</p><h1>Administration</h1><p>&nbsp;</p><p>&lt;?php<br /><br />                       $prev_admin_category = "";</p><p>&nbsp;</p><p>                        for($count_boucle_admin=0; $count_boucle_admin &lt;= $count_xml_elt ; $count_boucle_admin++) {<br />                            $category_admin= $array_admin_xml[$count_boucle_admin][0];<br />                            if ("$prev_admin_category" != "$category_admin"){<br />                                echo "&lt;hr&gt;&lt;br&gt;\n&lt;h3&gt;&lt;a name=\"admin_".$count_boucle_admin."\"&gt;&lt;/a&gt;".$category_admin."&lt;/h3&gt;&lt;br&gt;\n";<br />                            }<br />                            $prev_admin_category = $category_admin;<br />                            $name = $array_admin_xml[$count_boucle_admin][1];<br />                            $desc = $array_admin_xml[$count_boucle_admin][2];<br />                            $url = $array_admin_xml[$count_boucle_admin][3];<br />                            $admin_url =$array_admin_xml[$count_boucle_admin][4];<br />                            $url = str_replace ('localhost', $HTTP_SERVER_VARS["SERVER_NAME"], $url);<br />                            $admin_url = str_replace ('localhost', $HTTP_SERVER_VARS["SERVER_NAME"], $admin_url);<br /><br />                            echo "&lt;b&gt;".$name."&lt;/b&gt;&lt;br&gt;\n";<br />                            echo "&lt;a href=\"".$url."\"&gt;".$url."&lt;/a&gt;";<br />              