(97, 0, 'rss_1a5f760f2e2b48827d4974a60857e7c2', 'O:9:"MagpieRSS":19:{s:6:"parser";i:0;s:12:"current_item";a:0:{}s:5:"items";a:15:{i:0;a:7:{s:5:"title";s:16:"elfin on "eShop"";s:4:"link";s:52:"http://wordpress.org/extend/plugins/eshop/#post-5094";s:7:"pubdate";s:31:"Fri, 04 Apr 2008 13:39:30 +0000";s:2:"dc";a:1:{s:7:"creator";s:5:"elfin";}s:4:"guid";s:41:"5094@http://wordpress.org/extend/plugins/";s:11:"description";s:42:"An accessible Paypal Shopping Cart plugin.";s:7:"summary";s:42:"An accessible Paypal Shopping Cart plugin.";}i:1;a:7:{s:5:"title";s:37:"Seans0n on "WordPress Link Directory"";s:4:"link";s:71:"http://wordpress.org/extend/plugins/wordpress-link-directory/#post-7324";s:7:"pubdate";s:31:"Tue, 14 Oct 2008 21:09:14 +0000";s:2:"dc";a:1:{s:7:"creator";s:7:"Seans0n";}s:4:"guid";s:41:"7324@http://wordpress.org/extend/plugins/";s:11:"description";s:150:"WordPress Link Directory is a simple, compact and quick-setup link directory plugin for WordPress which allows other webmasters to add their site to y";s:7:"summary";s:150:"WordPress Link Directory is a simple, compact and quick-setup link directory plugin for WordPress which allows other webmasters to add their site to y";}i:2;a:7:{s:5:"title";s:27:"mg12 on "WP-RecentComments"";s:4:"link";s:64:"http://wordpress.org/extend/plugins/wp-recentcomments/#post-6185";s:7:"pubdate";s:31:"Fri, 04 Jul 2008 18:19:04 +0000";s:2:"dc";a:1:{s:7:"creator";s:4:"mg12";}s:4:"guid";s:41:"6185@http://wordpress.org/extend/plugins/";s:11:"description";s:45:"Display recent comments in your blog sidebar.";s:7:"summary";s:45:"Display recent comments in your blog sidebar.";}i:3;a:7:{s:5:"title";s:29:"joetan on "WordPress Reports"";s:4:"link";s:61:"http://wordpress.org/extend/plugins/tantan-reports/#post-7414";s:7:"pubdate";s:31:"Wed, 22 Oct 2008 18:46:23 +0000";s:2:"dc";a:1:{s:7:"creator";s:6:"joetan";}s:4:"guid";s:41:"7414@http://wordpress.org/extend/plugins/";s:11:"description";s:59:"Generates reports from Google Analytics and Feedburner data";s:7:"summary";s:59:"Generates reports from Google Analytics and Feedburner data";}i:4;a:7:{s:5:"title";s:18:"devoox on "Glassy"";s:4:"link";s:53:"http://wordpress.org/extend/plugins/glassy/#post-7443";s:7:"pubdate";s:31:"Sun, 26 Oct 2008 06:13:44 +0000";s:2:"dc";a:1:{s:7:"creator";s:6:"devoox";}s:4:"guid";s:41:"7443@http://wordpress.org/extend/plugins/";s:11:"description";s:54:"An automatic glassy effect for all your post''s images.";s:7:"summary";s:54:"An automatic glassy effect for all your post''s images.";}i:5;a:7:{s:5:"title";s:30:"robfelty on "Collapsing Links"";s:4:"link";s:63:"http://wordpress.org/extend/plugins/collapsing-links/#post-6692";s:7:"pubdate";s:31:"Mon, 18 Aug 2008 14:05:17 +0000";s:2:"dc";a:1:{s:7:"creator";s:8:"robfelty";}s:4:"guid";s:41:"6692@http://wordpress.org/extend/plugins/";s:11:"description";s:105:"This widget uses Javascript to dynamically expand or collapsable the set of\nlinks for each link category.";s:7:"summary";s:105:"This widget uses Javascript to dynamically expand or collapsable the set of\nlinks for each link category.";}i:6;a:7:{s:5:"title";s:28:"devil1591 on "Landing sites"";s:4:"link";s:60:"http://wordpress.org/extend/plugins/landing-sites/#post-7403";s:7:"pubdate";s:31:"Tue, 21 Oct 2008 22:22:08 +0000";s:2:"dc";a:1:{s:7:"creator";s:9:"devil1591";}s:4:"guid";s:41:"7403@http://wordpress.org/extend/plugins/";s:11:"description";s:131:"When visitors is referred to your site from a search engine, the plugin is showing them related posts to their search on your blog.";s:7:"summary";s:131:"When visitors is referred to your site from a search engine, the plugin is showing them related posts to their search on your blog.";}i:7;a:7:{s:5:"title";s:24:"satollo on "Hyper Cache"";s:4:"link";s:58:"http://wordpress.org/extend/plugins/hyper-cache/#post-5170";s:7:"pubdate";s:31:"Thu, 10 Apr 2008 12:16:30 +0000";s:2:"dc";a:1:{s:7:"creator";s:7:"satollo";}s:4:"guid";s:41:"5170@http://wordpress.org/extend/plugins/";s:11:"description";