(97, 0, 'rss_1a5f760f2e2b48827d4974a60857e7c2', 'O:9:"MagpieRSS":19:{s:6:"parser";i:0;s:12:"current_item";a:0:{}s:5:"items";a:15:{i:0;a:7:{s:5:"title";s:36:"whiteshadow on "Broken Link Checker"";s:4:"link";s:66:"http://wordpress.org/extend/plugins/broken-link-checker/#post-2441";s:7:"pubdate";s:31:"Mon, 08 Oct 2007 21:35:04 +0000";s:2:"dc";a:1:{s:7:"creator";s:11:"whiteshadow";}s:4:"guid";s:41:"2441@http://wordpress.org/extend/plugins/";s:11:"description";s:133:"This plugin will check your posts for broken links and missing images in background and notify you on the dashboard if any are found.";s:7:"summary";s:133:"This plugin will check your posts for broken links and missing images in background and notify you on the dashboard if any are found.";}i:1;a:7:{s:5:"title";s:25:"buzink on "Lifestream.fm"";s:4:"link";s:59:"http://wordpress.org/extend/plugins/lifestreamfm/#post-7387";s:7:"pubdate";s:31:"Tue, 21 Oct 2008 00:13:41 +0000";s:2:"dc";a:1:{s:7:"creator";s:6:"buzink";}s:4:"guid";s:41:"7387@http://wordpress.org/extend/plugins/";s:11:"description";s:60:"Provides a sidebar widget for your Lifestream.fm lifestream.";s:7:"summary";s:60:"Provides a sidebar widget for your Lifestream.fm lifestream.";}i:2;a:7:{s:5:"title";s:23:"mdawaffe on "PollDaddy"";s:4:"link";s:56:"http://wordpress.org/extend/plugins/polldaddy/#post-7331";s:7:"pubdate";s:31:"Wed, 15 Oct 2008 17:42:06 +0000";s:2:"dc";a:1:{s:7:"creator";s:8:"mdawaffe";}s:4:"guid";s:41:"7331@http://wordpress.org/extend/plugins/";s:11:"description";s:56:"Create and manage PollDaddy polls from within WordPress.";s:7:"summary";s:56:"Create and manage PollDaddy polls from within WordPress.";}i:3;a:7:{s:5:"title";s:23:"heart5 on "StatPressCN"";s:4:"link";s:58:"http://wordpress.org/extend/plugins/statpresscn/#post-7173";s:7:"pubdate";s:31:"Sun, 28 Sep 2008 10:30:45 +0000";s:2:"dc";a:1:{s:7:"creator";s:6:"heart5";}s:4:"guid";s:41:"7173@http://wordpress.org/extend/plugins/";s:11:"description";s:138:"Manage visit statistics of realtime, hot articles, random articles, feed subscriber''s count, etc; can be used by your blog through widget.";s:7:"summary";s:138:"Manage visit statistics of realtime, hot articles, random articles, feed subscriber''s count, etc; can be used by your blog through widget.";}i:4;a:7:{s:5:"title";s:27:"d13design on "d13slideshow"";s:4:"link";s:59:"http://wordpress.org/extend/plugins/d13slideshow/#post-7354";s:7:"pubdate";s:31:"Fri, 17 Oct 2008 09:18:42 +0000";s:2:"dc";a:1:{s:7:"creator";s:9:"d13design";}s:4:"guid";s:41:"7354@http://wordpress.org/extend/plugins/";s:11:"description";s:57:"Add animated slideshows of your featured pages and posts.";s:7:"summary";s:57:"Add animated slideshows of your featured pages and posts.";}i:5;a:7:{s:5:"title";s:30:"php4ever on "WordPress Realty"";s:4:"link";s:56:"http://wordpress.org/extend/plugins/wp-realty/#post-7382";s:7:"pubdate";s:31:"Mon, 20 Oct 2008 18:25:10 +0000";s:2:"dc";a:1:{s:7:"creator";s:8:"php4ever";}s:4:"guid";s:41:"7382@http://wordpress.org/extend/plugins/";s:11:"description";s:113:"WP Realty was developed to provide website owners with truly easy to manage integrated listings manager features.";s:7:"summary";s:113:"WP Realty was developed to provide website owners with truly easy to manage integrated listings manager features.";}i:6;a:7:{s:5:"title";s:72:"figmentthinking on "Figment Thinking Wordpress Database Table Optimizer"";s:4:"link";s:81:"http://wordpress.org/extend/plugins/wordpress-database-table-optimizer/#post-7351";s:7:"pubdate";s:31:"Fri, 17 Oct 2008 05:23:35 +0000";s:2:"dc";a:1:{s:7:"creator";s:15:"figmentthinking";}s:4:"guid";s:41:"7351@http://wordpress.org/extend/plugins/";s:11:"description";s:138:"The WordPress Database Table Optimizer plugin will automatically make sure that your WordPress MySQL database tables are always optimized.";s:7:"summary";s:138:"The WordPress Database Table Optimizer plugin will automatically make sure that your WordPress MySQL database tables are always optimized.";}i:7;a